# -*- coding: utf-8 -*-
class Solution(object):
    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        if len(words) == 0:
            return []

        m = len(words[0]) # 单词长度
        t = len(words) # 单词个数

        # 求得所有word 在 s中的下标
        indexs = []
        str2index = {}
        for i in range(len(s) - m + 1):
            str = s[i:i+m]
            if str2index.get(str,None ) == None:
                str2index[str] = []
            str2index[str].append(i)
        # word2index = {}
        # for word in words:
        #     if word not in str2index:
        #         return []
        #     word2index[word] = str2index[word]
        uniqueWords = set(words)
        for str in str2index.keys():
            if str in uniqueWords:
                indexs.extend(str2index[str])
        indexs.sort()
        # for i in range(len(indexs)):
        #     pass
        #indexs = set(indexs)
        #从所有word下标中选择符合条件的:
        ## 对所有单词计数，便于判断在word中的所有words被找到
        occur = {}
        for word in words:
            if occur.get(word,None) == None:
                occur[word] = 1
            else:
                occur[word] += 1

        ##依次选择indexs中的值，作为下标中的开始值
        res = []
        i = 0
        while i < len(indexs) - t + 1:
            start = indexs[i]
            visited = {}
            for word in uniqueWords:
                visited[word] = 0
            visited[s[start:start+m]] += 1
            cur = start
            j = i + 1
            k = 2
            while k <= t:
                cur += m
                while j < len(indexs):
                    if indexs[j] >= cur:
                        break;
                    else:
                        j += 1
                if j == len(indexs) or indexs[j] > cur:
                    break;
                else:
                    curStr = s[cur:cur + m]
                    if  visited[curStr] > occur[curStr]:
                        break
                    else:
                        visited[curStr] += 1
                k += 1
            if k > t:
                res.append(start)
            i += 1
        return res



